// CSS Modules
declare module "*.css" {
  const content: { [className: string]: string };
  export default content;
}

// Global CSS imports
declare module "*.css";

// SCSS Modules
declare module "*.scss" {
  const content: { [className: string]: string };
  export default content;
}

// SCSS
declare module "*.scss";

// LESS Modules
declare module "*.less" {
  const content: { [className: string]: string };
  export default content;
}

// LESS
declare module "*.less";

// Stylus Modules
declare module "*.styl" {
  const content: { [className: string]: string };
  export default content;
}

// Stylus
declare module "*.styl";
